CREATE TABLE ROL (
ID_ROL INT NOT NULL,
ROL VARCHAR (40) NOT NULL,
PRIMARY KEY(ID_ROL));

--------------------------------------------------

CREATE TABLE USUARIOS (
  ID_USUARIO INT NOT NULL,
  NOMBRE VARCHAR (45) NOT NULL,
  APELLIDO VARCHAR (45) NOT NULL,
  CORREO VARCHAR (45),
  CONTRASEÑA VARCHAR (45) NOT NULL,
  FECHA_INSCRIPCION DATE,
  ROL_ID INT NOT NULL,
  PRIMARY KEY (ID_USUARIO),
  FOREIGN KEY (ROL_ID) REFERENCES ROL (ID_ROL));
  
   CREATE TABLE PRODUCTOS (
  ID_PRODUCTO INT NOT NULL,
  REFERENCIA VARCHAR (45) NOT NULL,
  SERIAL VARCHAR (45) NOT NULL,
  PERFIL VARCHAR (45) NOT NULL,
  PRECIO INT NOT NULL,
  CANTIDAD INT NOT NULL,
  PRIMARY KEY (ID_PRODUCTO));
  
  
 CREATE TABLE PEDIDOS (
  ID_PEDIDO INT NOT NULL,
  USUARIO_ID INT NOT NULL,
  PRODUCTO_ID INT NOT NULL,
  CANTIDAD INT NOT NULL,
  PRECIO INT NOT NULL,
  PRIMARY KEY (ID_PEDIDO),
  FOREIGN KEY (USUARIO_ID) REFERENCES USUARIOS (ID_USUARIO),
  FOREIGN KEY (PRODUCTO_ID) REFERENCES PRODUCTOS (ID_PRODUCTO)
  );

  CREATE TABLE PERFIL_CLIENTES (
  ID_PERFIL_CLIENTE INT NOT NULL,
  PERFIL VARCHAR (45) NOT NULL,
  PRIMARY KEY (ID_PERFIL_CLIENTE));



  CREATE TABLE EQUIPOS (
  ID_EQUIPO INT NOT NULL,
  TIPO_EQUIPO VARCHAR (45) NOT NULL,
  PERFIL_EQUIPO VARCHAR (45) NOT NULL,
  MODELO VARCHAR (45) NOT NULL,
  CARACTERISTICAS VARCHAR (100) NOT NULL,
  PERFIL_CLIENTE_ID INT NOT NULL,
  PRECIO VARCHAR (45) NOT NULL,
  PRIMARY KEY (ID_EQUIPO),
  FOREIGN KEY (PERFIL_CLIENTE_ID) REFERENCES PERFIL_CLIENTES (ID_PERFIL_CLIENTE));
  
  

