SELECT EQUI.MODELO, EQUI.PERFIL_EQUIPO, EQUI.TIPO_EQUIPO, PER.PERFIL, EQUI.CARACTERISTICAS, EQUI.PRECIO
FROM EQUIPOS EQUI INNER JOIN PERFIL_CLIENTES PER
ON EQUI.PERFIL_CLIENTE_ID = PER.ID_PERFIL_CLIENTE;
WHERE EQUI.PERFIL_EQUIPO = 'ESTUDIO'  AND EQUI.TIPO_EQUIPO = 'PORTATIL' AND PER.PERFIL = 'ESTUDIANTE';
  
